// frontend/src/pages/Register.js
import { useState } from "react";
import axios from "axios";
import "./Auth.css";

function Register() {
  const [name, setName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [role, setRole] = useState("client");
  const [message, setMessage] = useState("");

  const handleRegister = async (e) => {
    e.preventDefault();
    setMessage("Submitting...");

    const payload = {
      // send both to match either backend expecting `name` or `username`
      name,
      username: name,
      email,
      password,
      role,
    };

    try {
      const res = await axios.post(
        "http://127.0.0.1:8000/api/users/register/",
        payload,
        { timeout: 10000 } // 10s timeout
      );

      // success (201)
      setMessage("Registration Successful!");
      console.log("Register response:", res.data);
    } catch (error) {
      // network error (no response)
      if (!error.response) {
        console.error("Network / CORS / server down error:", error.message);
        setMessage("Network error: cannot reach server (check backend or CORS).");
        return;
      }

      // server responded - show status + body
      const status = error.response.status;
      const data = error.response.data;
      console.error("Register failed:", status, data);

      // display friendly but useful message
      if (status === 400) {
        // validation errors
        setMessage("Validation error: " + JSON.stringify(data));
      } else if (status === 409) {
        setMessage("Conflict: " + JSON.stringify(data));
      } else {
        setMessage("Server error: " + JSON.stringify(data));
      }
    }
  };

  return (
    <div className="auth-container">
      <div className="auth-card">
        <h2 className="title">Register</h2>

        {message && <p style={{ wordBreak: "break-word" }}>{message}</p>}

        <form onSubmit={handleRegister}>
          <input
            type="text"
            className="auth-input"
            placeholder="Full Name"
            value={name}
            onChange={(e) => setName(e.target.value)}
          />

          <input
            type="email"
            className="auth-input"
            placeholder="Email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
          />

          <input
            type="password"
            className="auth-input"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
          />

          <select
            className="auth-input"
            value={role}
            onChange={(e) => setRole(e.target.value)}
          >
            <option value="client">Client</option>
            <option value="freelancer">Freelancer</option>
          </select>

          <button type="submit" className="auth-btn">Register</button>
        </form>
      </div>
    </div>
  );
}

export default Register;
