import { useState } from "react";

export default function AuthPage() {
  const [activeTab, setActiveTab] = useState("login");

  const cardStyle = {
    width: "380px",
    padding: "30px",
    background: "white",
    borderRadius: "15px",
    boxShadow: "0 8px 24px rgba(0,0,0,0.1)",
    textAlign: "center"
  };

  const inputStyle = {
    width: "100%",
    padding: "12px",
    marginTop: "10px",
    borderRadius: "8px",
    border: "1px solid #d4d4d4",
    fontSize: "15px"
  };

  const buttonStyle = {
    width: "100%",
    padding: "12px",
    marginTop: "20px",
    borderRadius: "8px",
    border: "none",
    background: "#6c63ff",
    color: "white",
    fontSize: "16px",
    cursor: "pointer"
  };

  return (
    <div style={{ height: "100vh", background: "#f4f6fb" }}>
      
      {/* Top header */}
      <div style={{
        padding: "18px 0",
        background: "linear-gradient(90deg, #6C63FF, #A566FF)",
        color: "white",
        fontSize: "26px",
        fontWeight: "bold",
        textAlign: "center"
      }}>
        TalentLink
      </div>

      {/* Container */}
      <div style={{
        display: "flex",
        justifyContent: "center",
        alignItems: "center",
        height: "80vh"
      }}>
        
        <div style={cardStyle}>
          
          {/* Tabs */}
          <div style={{ display: "flex", marginBottom: "20px" }}>
            <div
              onClick={() => setActiveTab("login")}
              style={{
                flex: 1,
                padding: "10px",
                cursor: "pointer",
                borderBottom: activeTab === "login" ? "3px solid #6C63FF" : "1px solid #ccc",
                fontWeight: activeTab === "login" ? "bold" : "normal"
              }}
            >
              Login
            </div>

            <div
              onClick={() => setActiveTab("register")}
              style={{
                flex: 1,
                padding: "10px",
                cursor: "pointer",
                borderBottom: activeTab === "register" ? "3px solid #6C63FF" : "1px solid #ccc",
                fontWeight: activeTab === "register" ? "bold" : "normal"
              }}
            >
              Register
            </div>
          </div>

          {/* Login Form */}
          {activeTab === "login" && (
            <>
              <h3>Login</h3>
              <input style={inputStyle} type="email" placeholder="Email" />
              <input style={inputStyle} type="password" placeholder="Password" />
              <button style={buttonStyle}>Login</button>
            </>
          )}

          {/* Register Form */}
          {activeTab === "register" && (
            <>
              <h3>Register</h3>
              <input style={inputStyle} type="text" placeholder="Full Name" />
              <input style={inputStyle} type="email" placeholder="Email" />
              <input style={inputStyle} type="password" placeholder="Password" />
              <select style={inputStyle}>
                <option value="client">Client</option>
                <option value="freelancer">Freelancer</option>
              </select>
              <button style={buttonStyle}>Register</button>
            </>
          )}

        </div>

      </div>
    </div>
  );
}
